$Object1 = Invoke-WebRequest -Uri 'https://im.qq.com/pcqq/index.shtml'
$Global:DumplingsStorage.QQApps = curl -fsSL --resolve "cdn-go.cn:443:$(Resolve-DnsName -Name 'cdn-go.cn' -Server '114.114.114.114' -Type 'A' -DnsOnly | Where-Object -FilterScript { $_ -is [Microsoft.DnsClient.Commands.DnsRecord_A] } | Select-Object -ExpandProperty 'IP4Address' -First 1)" ('https:' + [regex]::Match($Object1.Content, 'rainbowConfigUrl\s*=\s*"(.+?)"').Groups[1].Value) | Join-String -Separator "`n" | Get-EmbeddedJson -StartsFrom 'var params= ' | ConvertFrom-Json
